WebLink.url:
{!REQUIRESCRIPT('/soap/ajax/29.0/connection.js')}

try{
    var recToUpdate = new sforce.SObject("Twenty_Groups__c");
    recToUpdate.Id = "{!Twenty_Groups__c.Id}";
    recToUpdate.Print_Count_Dealership_Form__c =
       {!Twenty_Groups__c.Print_Count_Dealership_Form__c} + 1;

    var result = sforce.connection.update([recToUpdate]);

if(result[0].success == 'true'){
        alert('The Dealership Data Form has been attached to this record successfully.');
    }
    else{
        alert('An Error has Occurred. Error:' + result[0].errors.message);
    }
}
catch(e){
    alert('An Error has Occurred. Error: ' + e);
}
